bot.on("message", message => {
    if (!message.guild) return;
    if (message.content.startsWith("?warn")) {
    var argomenti = message.content.slice(5).trim().split(' ');
    var utente = message.mentions.members.first();

    if (!message.member.hasPermission("MANAGE_MESSAGES")) {
        message.channel.send('Non hai il permesso');
        return;
    }

    if (!utente) {
        message.channel.send("Non hai taggato un utente")
        return
    }

    if (argomenti.length == 1) {
        message.channel.send("Non hai inserito una motivazione.")
        return
    }

    var reason = "";
    for (var i = 1; i < argomenti.length; i++) {
        reason += argomenti[i] + " ";
    }

     utente.send(utente.toString() + " Sei stato avvisato da " +  message.author.toString() + " \nPer " + reason + "").then(message.channel.send(utente.toString() + " É stato avvisato da " + message.author.toString() + "    \n\n𝗥𝗲𝗮𝘀𝗼𝗻: " + reason + ""))

 }
 })